function getBakeOffImages() {
	var images = [];
	for (var i = 0; i < arguments.length; i++) {
		var image = arguments[i];
		if (image) {
			var url = image;
			if (url.indexOf("http") === 0) {
				url = "https:" + url;
			}
			images.push(url);
		}
	}
	return images;
}

function getBakeOffVideos() {
	var videos = [];
	for (var i = 0; i < arguments.length; i++) {
		var video = arguments[i];
		if (video) {
			video = video.replace(/https?:\/\/.*\.(?:v|m)\.\w+/, "");
			videos.push(video);
		}
	}
	return videos;
}
