function getNews() {
	var url = 'http://api.bilibili.com/x/web-interface/api/news/get_news';
	var data = JSON.stringify({
		"page": 1,
		"limit": 10,
		"keyword": "",
		"sort": "desc",
		"order": "asc",
		"from": "now",
		"to": "now"
	});
	var xhr = new XMLHttpRequest();
	xhr.open("GET", url, true);
	xhr.onload = function () {
		if (xhr.status == 200) {
			var json = JSON.parse(xhr.responseText);
			for (var i = json.data.length - 1; i >= 0; i--) {
				var news = json.data[i];
				console.log(news);
			}
		} else {
			alert('请求失败');
		}
	};
	xhr.send(data);
}

